import { useNavigate } from "react-router-dom";
import { useEffect, useRef, useState } from "react";
import axios from "axios";
import ErrorPage from "./ErrorPage";
import { Link } from "react-router-dom";
const Allphotos = () => {
  const [imgs, setImgs] = useState([]);

  const navigate = useNavigate();
  const cloudinaryRef = useRef();
  const widgetRef = useRef();

  useEffect(() => {
    if (!localStorage.getItem("token")) {
      navigate('/signup');
    }
  }, [navigate]);

  useEffect(() => {
    cloudinaryRef.current = window.cloudinary;
    widgetRef.current = cloudinaryRef.current.createUploadWidget(
      {
        cloudName: "dli5g7kbs",
        uploadPreset: "ltcyvgkv",
      },
      function (err, result) {
        if (result && result.event === "success" && result.info.secure_url) {
          const apiUrl = 'http://localhost:3000/upload';
          const headers = {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer ' + localStorage.getItem("token"),
          };
          const imgLink = result.info.secure_url;

          const postImage = async () => {
            try {
              await axios.post(apiUrl, { img: imgLink }, { headers: headers });
              console.log("Image uploaded successfully");
              // Fetch images again after uploading
              fetchImages();
            } catch (error) {
              console.error("Error uploading image:", error);

            }
          };

          if (localStorage.getItem("token")) {
            postImage();
          }
        }
      }
    );
  }, []);

  useEffect(() => {
    if (localStorage.getItem("token")) {
      fetchImages();
    }
  }, []);

  const fetchImages = async () => {
    try {
      const res = await axios.get('http://localhost:3000/photos', {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer ' + localStorage.getItem("token"),
        }
      });
      const data = res.data;
      setImgs(data.images);
      console.log(data.images);
    } catch (error) {
      console.error("Error fetching images:", error);
      <ErrorPage></ErrorPage>
    }
  };

  return (
    <>
      {imgs.length > 0 && 
        <div id="carouselExampleControls" className="carousel slide" data-ride="carousel">
          <div className="carousel-inner">
            {imgs.map((img, index) => (
              <div key={index} className={`carousel-item ${index === 0 ? 'active' : ''}`}>
                <img className="d-block w-100" src={img} alt={`Slide ${index + 1}`} />
              </div>
            ))} 
          </div>
          <a className="carousel-control-prev" href="#carouselExampleControls" role="button" data-slide="prev">
            <span className="carousel-control-prev-icon" aria-hidden="true"></span>
            <span className="sr-only">Previous</span>
          </a>
          <a className="carousel-control-next" href="#carouselExampleControls" role="button" data-slide="next">
            <span className="carousel-control-next-icon" aria-hidden="true"></span>
            <span className="sr-only">Next</span>
          </a>
        </div>
      }
      <button onClick={() => { widgetRef.current.open() }}>Upload</button>

    </>);
}
export default Allphotos;
