import { useNavigate } from "react-router-dom";
import { useEffect, useRef } from "react";
import axios from "axios";
import ErrorPage from "./ErrorPage";

const Allphotos = () => {
  const navigate = useNavigate();

  useEffect(() => {
    if (!localStorage.getItem("token")) {
      navigate('/signup');
    }
  }, [navigate]);

  const cloudinaryRef = useRef();
  const widgetref = useRef();

  useEffect(() => {
    cloudinaryRef.current = window.cloudinary;
    widgetref.current = cloudinaryRef.current.createUploadWidget(
      {
        cloudName: "dli5g7kbs",
        uploadPreset: "ltcyvgkv",
      },
      function (err, result) {
        if (result && result.event === "success" && result.info.secure_url) {
          const apiUrl = 'http://localhost:3000/upload';
          const headers = {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer ' + localStorage.getItem("token"),
          };
          const imglink = result.info.secure_url;

          const posty = async () => {
            console.log("Link->", imglink);
            try {
              const response = await axios.post(apiUrl, { img: imglink }, { headers: headers });
              console.log("1", imglink);
            } catch (e) {
              <ErrorPage></ErrorPage>
            }
          };
          if(localStorage.getItem("token")){
            posty();
          }
        }
      }
    );
  }, []);

  return (
    <>
      <button onClick={() => { widgetref.current.open() }}>upload</button>
    </>
  );
};

export default Allphotos;
